mapper.select:
SELECT WEBTOON_NO , WEBTOON_NAME , SERIAL_CODE , WRITER , ILLUSTRATOR , GENRE_CODE FROM TB_WEBTOON
JOIN TB_GENRE USING (GENRE_CODE) WHERE GENRE_NAME = #{ value }
JOIN TB_SERIALIZATION USING (SERIAL_CODE) WHERE SERIAL_DATE = #{ value }
ORDER BY WEBTOON_NO

mapper.insert:
INSERT
	INTO TB_WEBTOON
  (
     WEBTOON_NO
   , WEBTOON_NAME
   , SERIAL_CODE
   , WRITER
   , ILLUSTRATOR
   , GENRE_CODE   
  )
	VALUES
  (
  	 SEQ_WEBTOON_NO.NEXTVAL
   , #{ webtoonName }
   , #{ serialCode }
   , #{ writer }
   , #{ illustrator }
   , #{ genreCode }	 
  )

mapper.update:
	UPDATE
	       TB_WEBTOON
	   SET WEBTOON_NAME = #{ webtoonName }
       , SERIAL_CODE = #{ serialCode }
       , WRITER = #{ writer }
       , ILLUSTRATOR = #{ illustrator } 
       , GENRE_CODE = #{ genreCode }
   WHERE WEBTOON_NO = #{ webtoonNo }

mapper.delete:
DELETE
  FROM TB_WEBTOON
 WHERE WEBTOON_NAME = #{ webtoonName }
